var win  = this.editorElement.contentWindow;
var doc  = this.contentDocument;

var y  = doc.getBoxObjectFor(node).y;
var yo = win.pageYOffset;
var h  = win.innerHeight;
// dump("  h: " + h + ", y: " + y + ", yo: " + yo + "\n");
if (y >= yo + (h * 0.9)) {
  // dump("  scrolling\n");
  win.scrollTo(0, y - h / 2);
}


