var ed  = this.editor;
var doc = this.contentDocument;

try {
  ed.setShouldTxnSetSelection(false);

  var nd, i, pnode;

  // Leading paren
  for (i = 0; i < block.childNodes.length; i++) {
    nd = block.childNodes[i];
    if (nd.nodeType == nd.TEXT_NODE && nd.nodeValue.match(/^\s*\(/)) {
      pnode = { value: null };
      ed.splitNode(nd, nd.nodeValue.indexOf("(") + 1, pnode);
      if (pnode.value) ed.deleteNode(pnode.value);
      break;
    }
  }

  // Trailing paren
  for (i = block.childNodes.length - 1; i >= 0; i--) {
    nd = block.childNodes[i];
    if (nd.nodeType == nd.TEXT_NODE && nd.nodeValue.match(/\)\s*$/)) {
      pnode = { value: null };
      ed.splitNode(nd, nd.nodeValue.lastIndexOf(")"), pnode);
      if (pnode.value) ed.deleteNode(nd);
      break;
    }
  }

}
catch (ex) {
  dump("deparenthesize: " + ex + "\n");
}
finally {
  ed.setShouldTxnSetSelection(true);
}


// Adapted from ComposerCommands.js
try {
  var cmdParams = Components.classes[
    "@mozilla.org/embedcomp/command-params;1"]
    .createInstance(Components.interfaces.nsICommandParams);
  var dispatcher = document.commandDispatcher;
  var cmd = 'cmd_' + style;
  var ctl = dispatcher.getControllerForCommand(cmd);
  if (ctl && ctl.isCommandEnabled(cmd)) {
    if (ctl instanceof Components.interfaces.nsICommandController) {
      ctl.doCommandWithParams(cmd, cmdParams);
      if (cmdParams)
        ctl.getCommandStateWithParams(cmd, cmdParams);
    }
    else
      ctl.doCommand(cmd);
  }
  else {
    if (ctl)
      dump("*** toggleStyle: " + cmd + " not enabled\n");
    else
      dump("*** toggleStyle: No controller for " + cmd + "\n");
  }
}
catch (ex) {
  dump("*** toggleStyle: " + ex + "\n");
}


if (this.selection.isCollapsed) {
  dump("*** script.xml::markup called for empty selection\n");
  return;
}
var editor = this.editor;

if (! this.isLocked) {
  editor.beginTransaction();
  try {
    // We need to preserve all the notes/media in between, because
    // they lose their class attributes (and get a ref attribute)
    // as a result of this
    var notes = new Array();
    var media = new Array();
    for (var i = 0; i < this.selection.rangeCount; ++i) {
      var range = this.selection.getRangeAt(i);
      var start = range.startContainer;
      if (start.hasChildNodes() &&
          range.startOffset < start.childNodes.length)
        start = start.childNodes[range.startOffset];
      var iter = Components.classes["@celtx.com/dom/iterator;1"]
        .createInstance(Components.interfaces.celtxINodeIterator);
      iter.init(start, range);
      var node = null;
      while ((node = iter.nextNode()) != null) {
        if (node.nodeName.toLowerCase() == "span") {
          if (node.getAttribute("class") == "note")
            notes.push(node);
          else if (node.getAttribute("class") == "media")
            media.push(node);
        }
      }
    }
    var span = getAtom("span");
    editor.setInlineProperty(span, "class", type);
    editor.setInlineProperty(span, "ref", ref);

    for (var i = 0; i < notes.length; ++i) {
      var span = notes[i];
      editor.setAttribute(span, "class", "note");
      editor.removeAttribute(span, "ref");
    }
    for (var i = 0; i < media.length; ++i) {
      var span = media[i];
      editor.setAttribute(span, "class", "media");
      editor.removeAttribute(span, "ref");
    }
  }
  catch (ex) {
    dump("*** scripteditor.markup: " + ex + "\n");
  }
  editor.endTransaction();
}
else {
  try {
    var range = this.selection.getRangeAt(0);
    var IPara = Components.interfaces.nsIDOMHTMLParagraphElement;
    var startpara = range.startContainer;
    var endpara = range.endContainer;
    while (startpara && ! (startpara instanceof IPara))
      startpara = startpara.parentNode;
    while (endpara && ! (endpara instanceof IPara))
      endpara = endpara.parentNode;

    if (! startpara)
      throw new Error("Could not locate start paragraph");
    if (! endpara)
      throw new Error("Could not locate end paragraph");

    if (startpara == endpara) {
      var span = this.contentDocument.createElement("span");
      span.setAttribute("class", type);
      span.setAttribute("ref", ref);
      range.surroundContents(span);
    }
    else {
      var startrange = range.cloneRange();
      startrange.setEndBefore(startpara.lastChild);

      var endrange = range.cloneRange();
      endrange.setStart(endpara, 0);

      var midparas = [];
      var nextpara = nextElement(startpara);
      while (nextpara && nextpara != endpara) {
        if (nextpara instanceof IPara)
          midparas.push(nextpara);
        nextpara = nextElement(nextpara);
      }

      var startspan = this.contentDocument.createElement("span");
      startspan.setAttribute("class", type);
      startspan.setAttribute("ref", ref);
      startrange.surroundContents(startspan);

      var endspan = this.contentDocument.createElement("span");
      endspan.setAttribute("class", type);
      endspan.setAttribute("ref", ref);
      endrange.surroundContents(endspan);

      for (var i = 0; i < midparas.length; ++i) {
        var midrange = this.contentDocument.createRange();
        midrange.setStart(midparas[i], 0);
        midrange.setEndBefore(midparas[i].lastChild);

        var midspan = this.contentDocument.createElement("span");
        midspan.setAttribute("class", type);
        midspan.setAttribute("ref", ref);
        midrange.surroundContents(midspan);
      }
    }
    this.incrementModificationCount(1);
  }
  catch (ex) {
    dump("*** scripteditor[locked].markup: " + ex + "\n");
  }
}


if (! this.isLocked) {
