  this.editor.beginTransaction();
try {
  var sel = this.selection;
  var str = "//span[@ref]";
  var xpath = new XPathEvaluator();
  var result = xpath.evaluate(str, this.contentDocument.body,
    null, XPathResult.ORDERED_NODE_ITERATOR_TYPE, null);
  var spans = [];
  var span = null;
  var foundStart = false;
  while ((span = result.iterateNext()) != null) {
    if (sel.containsNode(span, true)) {
      foundStart = true;
      spans.push(span);
    }
    else if (foundStart)
      // Past the end of the selection
      break;
  }
  // Shifting around DOM nodes without the editor fix-up causes
  // the selection to look weird.
  if (this.isLocked)
    sel.collapseToStart();
  for (var i = 0; i < spans.length; ++i)
    this.unmarkupSpan(spans[i]);
}
catch (ex) {
  dump("*** scripteditor.unmarkup: " + ex + "\n");
}
if (! this.isLocked)
  this.editor.endTransaction();


