      if (offset > 0) {
        var pretext = this.contentDocument.createTextNode(
          node.nodeValue.substring(0, offset));
        node.parentNode.insertBefore(pretext, node);
        node.nodeValue = node.nodeValue.substring(offset);
      }
    }
    else {
      throw new Error("Selection does not correspond to a text node ("
        + node.nodeName + ")");
    }
    node.parentNode.insertBefore(noteElem, node);
    this.incrementModificationCount(1);
  }
  else
    this.editor.insertElementAtSelection(noteElem, false);
