// The XMLSerializer turns span tags with no child nodes into
// empty <span/> tags, which are valid XML but not valid HTML,
// resulting in them being treated as open <span> tags when
// read back in. This turns them into <span></span> pairs.
function fixEmptySpans (str) {
  return str.replace(/(<SPAN [^>]+)\/>/g, "$1></SPAN>");
}

